import { createSlice } from "@reduxjs/toolkit";

const initialState = {
  question: {},
  language: "",
};

const quizSlice = createSlice({
  name: "quizSlice",
  initialState,
  reducers: {},
});

export const quizAction = quizSlice.actions;

export default quizSlice.reducer;
